css:
 @media print {
      body {
        font-family: 'Courier New', monospace;
        font-size: 12px;
        width: 72mm;
        margin: 0;
        padding: 0;
      }

      h2, p, h4 {
        margin: 2px 0;
        padding: 0;
      }

      table {
        width: 100%;
        border-collapse: collapse;
        font-size: 12px;
      }

      th, td {
        text-align: left;
        padding: 4px 0;
      }

      th {
        font-weight: bold;
        border-bottom: 1px solid black;
      }

      .centrado {
        text-align: center;
      }

      .totales {
        text-align: right;
        font-weight: bold;
        margin-top: 10px;
      }

      hr {
        border: none;
        border-top: 1px dashed #000;
        margin: 6px 0;
      }

      .firma {
        text-align: center;
        font-size: 10px;
        margin-top: 20px;
      }

      .page-break {
        page-break-after: always;
      }
    }

    body {
      font-family: 'Courier New', monospace;
      font-size: 12px;
      max-width: 72mm;
      margin: auto;
      color: black;
    }

html:
{% set src = inv if inv else doc %}
 {# Tipo de orden a nivel de cabecera #}
{% set order_type = (src.get("type_orden") or doc.get("type_orden")) %}
{% set order_type_uc = (order_type or "") %}
 {# ------------------ COMANDA ------------------ #}
  <h2 class="centrado">
  COMANDA{% if order_type_uc %} — {{ order_type_uc | upper }}{% endif %}
</h2>

{% if doc.alias %}<p><strong>Alias:</strong> {{ doc.alias }}</p>{% endif %}
<p><strong>Orden:</strong> {{ doc.name }}</p>
<p><strong>Fecha:</strong> {{ frappe.utils.format_datetime(doc.creation, "dd/MM/yyyy HH:mm") }}</p>
{% if doc.table %}<p><strong>Mesa:</strong> {{ doc.table }}</p>{% endif %}

{% if order_type_uc == "Domicilio" %}
  {% set del_addr = doc.get("delivery_address") %}
  {% set del_phone = doc.get("delivery_phone") %}
  {% if del_addr %}<p><strong>Dirección entrega:</strong> {{ del_addr }}</p>{% endif %}
  {% if del_phone %}<p><strong>Teléfono entrega:</strong> {{ del_phone }}</p>{% endif %}
{% endif %}
  <hr>
  <table>
    <thead>
      <tr>
        <th>Producto</th>
        <th style="text-align: center;">Cantidad</th>
      </tr>
    </thead>
    <tbody>
      {% for item in doc.items %}
        {% set producto = frappe.get_doc("Producto", item.product) %}
        <tr>
          <td>{{ producto.nombre }}</td>
          <td style="text-align: center;">{{ item.qty }}</td>
        </tr>
      {% endfor %}
    </tbody>
  </table>
  <br><br>
  <p class="firma">PREPARAR Y ENTREGAR</p>
