css:
body {
  font-family: "Helvetica", "Arial", sans-serif;
  font-size: 9px;
  color: #333;
  margin: 0;
  padding: 0;
  background: #f5f5f5;
}

.container {
  background: #fff;
  padding: 25px;
  border: 1px solid #ccc;
  width: 800px;
  margin: 20px auto;
}

.flex-container {
  display: flex;
  justify-content: space-between;
  align-items: flex-start;
  margin-bottom: 15px;
}

.company-section {
  width: 55%;
}

.company-section img {
  max-height: 60px;
  margin-bottom: 5px;
}

.sri-section {
  width: 40%;
  border: 1px solid #000;
  padding: 10px;
  font-size: 10px;
  background-color: #f3f3f3;
  text-align: center;
}

.barcode {
  text-align: center;
  margin-top: 15px;
}

.barcode img {
  height: 50px;
  margin-top: 5px;
}

.info-table,
.items-table,
.totals-table,
.payments-table {
  width: 100%;
  border-collapse: collapse;
  margin-top: 7px;
  font-size: 10.5px;
}

.info-table td,
.items-table th, .items-table td,
.totals-table td,
.payments-table td {
  border: 1px solid #ccc;
  padding: 5px;
}

.items-table th,
.payments-table th {
  background-color: #eaeaea;
  font-weight: bold;
  text-align: left;
    font-size: 10.5px;
}

.text-right {
  text-align: right;
}

.footer {
  font-size: 9px;
  color: #666;
  text-align: center;
  margin-top: 20px;
}
.summary-section {
  display: flex;
  justify-content: space-between;
  gap: 10px;
  margin-top: 15px;
}

.summary-column {
  width: 48%;
}

.totals-table td, .payments-table td, .payments-table th {
  font-size: 10.5px;
  padding: 6px;
}

.payments-table th {
  background-color: #eaeaea;
  text-align: left;
}
