css:
 @media print {
      body {
        font-family: 'Courier New', monospace;
        font-size: 12px;
        width: 72mm;
        margin: 0;
        padding: 0;
      }

      h2, p, h4 {
        margin: 2px 0;
        padding: 0;
          font-size: 10px;
      }

table {
  width: 100%;
  border-collapse: collapse;
  font-size: 10px;
}

th, td {
  text-align: left;
  padding: 2px 0;       /* Reducido */
  line-height: 1.2;     /* Más compacto */
}

th {
  font-weight: bold;
  border-bottom: 1px solid black;
}


      .centrado {
        text-align: center;
      }

      .totales {
        text-align: right;
        font-weight: bold;
        margin-top: 10px;
      }

      hr {
        border: none;
        border-top: 1px dashed #000;
        margin: 6px 0;
      }

      .firma {
        text-align: center;
        font-size: 10px;
        margin-top: 20px;
      }

      .page-break {
        page-break-after: always;
      }
    }

    body {
      font-family: 'Courier New', monospace;
      font-size: 12px;
      max-width: 72mm;
      margin: auto;
      color: black;
    }
