css:
 @media print {
      body {
        font-family: 'Courier New', monospace;
        font-size: 12px;
        width: 72mm;
        margin: 0;
        padding: 0;
      }

      h2, p, h4 {
        margin: 2px 0;
        padding: 0;
          font-size: 10px;
      }

table {
  width: 100%;
  border-collapse: collapse;
  font-size: 10px;
}

th, td {
  text-align: left;
  padding: 2px 0;       /* Reducido */
  line-height: 1.2;     /* Más compacto */
}

th {
  font-weight: bold;
  border-bottom: 1px solid black;
}


      .centrado {
        text-align: center;
      }

      .totales {
        text-align: right;
        font-weight: bold;
        margin-top: 10px;
      }

      hr {
        border: none;
        border-top: 1px dashed #000;
        margin: 6px 0;
      }

      .firma {
        text-align: center;
        font-size: 10px;
        margin-top: 20px;
      }

      .page-break {
        page-break-after: always;
      }
    }

    body {
      font-family: 'Courier New', monospace;
      font-size: 12px;
      max-width: 72mm;
      margin: auto;
      color: black;
    }

html:
{# ------------------ NOTA DE VENTA / FACTURA ------------------ #}

{# Multiempresa: toma la compañía desde el documento #}
{% set company = frappe.get_doc("Company", doc.company_id) %}

{% if company.logo %}
  <div style="text-align: center; margin-bottom: 10px;">
    <img src="{{ company.logo }}" alt="Logo" style="height: 80px;">
  </div>
{% endif %}

{% if doc.tipo_comprobante == "1" %}
  <h2 class="centrado">FACTURA</h2>
{% else %}
  <h2 class="centrado">NOTA DE VENTA</h2>
{% endif %}

{% if doc.estado == "Factura" %}
  <div style="font-size: 10px; line-height: 1.4;">
    <p><strong>RUC Emisor:</strong> {{ company.ruc or 'N/A' }}</p>
    <p><strong>Dirección:</strong> {{ company.address or 'N/A' }}</p>
    <p><strong>Factura:</strong> {{ doc.estab }}-{{ doc.ptoemi }}-{{ doc.secuencial }}</p>
    <p><strong>Clave de Acceso:</strong> <span style="word-break: break-word;">{{ doc.clave_acceso }}</span></p>
    <p><strong>F. Autorización:</strong> {{ doc.fecha_autorizacion or '' }}</p>
  </div>
{% endif %}

<div style="font-size: 10px; line-height: 1.4;">
  <p><strong>Fec. Emisión:</strong> {{ doc.fecha_emision or doc.creation }}</p>

  {% set cliente = frappe.get_doc("Cliente", doc.customer) %}
  <hr>
  <p><strong>Cliente:</strong> {{ cliente.nombre }}</p>
  <p><strong>Identificación:</strong> {{ cliente.num_identificacion }}</p>
  <p><strong>Correo:</strong> {{ cliente.correo }}</p>
  <p><strong>Teléfono:</strong> {{ cliente.telefono }}</p>
  <p><strong>Dirección:</strong> {{ cliente.direccion }}</p>
  {% if doc.alias %}
    <p class="centrado"><strong>Alias:</strong> {{ doc.alias }}</p>
  {% endif %}
  <p class="centrado"><strong>Orden:</strong> {{ doc.name }}</p>
</div>

<hr>

{# =======================
   ACUMULADORES POR TASA
   ======================= #}
{% set ns = namespace(
  base0=0.0, base5=0.0, base12=0.0, base13=0.0, base14=0.0, base15=0.0,
  iva0=0.0,  iva5=0.0,  iva12=0.0,  iva13=0.0,  iva14=0.0,  iva15=0.0,
  subtotal=0.0, ivatotal=0.0
) %}

<table>
  <thead>
    <tr>
      <th>Producto</th>
      <th style="text-align: center;">Cant</th>
      <th style="text-align: right;">Precio</th>
      <th style="text-align: right;">Total</th>
    </tr>
  </thead>
  <tbody>
    {% for item in doc.items %}
      {# % IVA desde DocType 'taxes' (multiempresa no afecta, es por ítem) #}
      {% set tax_value = (frappe.db.get_value('taxes', item.tax, 'value') or 0) | float %}

      {# Base sin IVA: usa item.total si ya viene neto; si no, qty*rate #}
      {% set base = (item.total if item.total is not none else (item.qty * item.rate)) | float %}
      {% set iva_line = (base * tax_value / 100.0) | float %}

      {# Acumula #}
      {% set ns.subtotal = ns.subtotal + base %}
      {% set ns.ivatotal = ns.ivatotal + iva_line %}
      {% if tax_value|int == 0 %}
        {% set ns.base0 = ns.base0 + base %}{% set ns.iva0 = ns.iva0 + iva_line %}
      {% elif tax_value|int == 5 %}
        {% set ns.base5 = ns.base5 + base %}{% set ns.iva5 = ns.iva5 + iva_line %}
      {% elif tax_value|int == 12 %}
        {% set ns.base12 = ns.base12 + base %}{% set ns.iva12 = ns.iva12 + iva_line %}
      {% elif tax_value|int == 13 %}
        {% set ns.base13 = ns.base13 + base %}{% set ns.iva13 = ns.iva13 + iva_line %}
      {% elif tax_value|int == 14 %}
        {% set ns.base14 = ns.base14 + base %}{% set ns.iva14 = ns.iva14 + iva_line %}
      {% elif tax_value|int == 15 %}
        {% set ns.base15 = ns.base15 + base %}{% set ns.iva15 = ns.iva15 + iva_line %}
      {% endif %}

      {% set producto = frappe.get_doc("Producto", item.product) %}
      <tr>
        <td>{{ producto.nombre }}</td>
        <td style="text-align: center;">{{ item.qty }}</td>
        <td style="text-align: right;">${{ "{:,.2f}".format(item.rate or 0) }}</td>
        <td style="text-align: right;">${{ "{:,.2f}".format(base) }}</td>
      </tr>
    {% endfor %}
  </tbody>
</table>

<hr>

{# ========== TOTALES DINÁMICOS POR TASA ========== #}
{% if ns.base15 > 0 %}
  <p class="totales">Subtotal 15%: ${{ "{:,.2f}".format(ns.base15) }}</p>
{% endif %}
{% if ns.base14 > 0 %}
  <p class="totales">Subtotal 14%: ${{ "{:,.2f}".format(ns.base14) }}</p>
{% endif %}
{% if ns.base13 > 0 %}
  <p class="totales">Subtotal 13%: ${{ "{:,.2f}".format(ns.base13) }}</p>
{% endif %}
{% if ns.base12 > 0 %}
  <p class="totales">Subtotal 12%: ${{ "{:,.2f}".format(ns.base12) }}</p>
{% endif %}
{% if ns.base5 > 0 %}
  <p class="totales">Subtotal 5%: ${{ "{:,.2f}".format(ns.base5) }}</p>
{% endif %}
{% if ns.base0 > 0 %}
  <p class="totales">Subtotal 0%: ${{ "{:,.2f}".format(ns.base0) }}</p>
{% endif %}

<p class="totales">Subtotal: ${{ "{:,.2f}".format(ns.subtotal) }}</p>
<p class="totales">IVA: ${{ "{:,.2f}".format(ns.ivatotal) }}</p>
<p class="totales"><strong>Total: ${{ "{:,.2f}".format(ns.subtotal + ns.ivatotal) }}</strong></p>

{# Si quieres, deja también los del doc para auditoría:
<p class="totales" style="opacity:.6">[Sistema] Subtotal: ${{ "{:,.2f}".format(doc.subtotal or 0) }}</p>
<p class="totales" style="opacity:.6">[Sistema] IVA: ${{ "{:,.2f}".format(doc.iva or 0) }}</p>
<p class="totales" style="opacity:.6">[Sistema] Total: ${{ "{:,.2f}".format(doc.total or 0) }}</p>
#}

{# ========== (Opcional) Formas de Pago con monto por fila ========== #}
{% if doc.payments and doc.payments|length > 0 %}
  <h4>Formas de Pago:</h4>
  <ul>
    {% for p in doc.payments %}
      {# Maestro del método #}
      {% set pago_dt = 'payments' if frappe.db.exists('payments', p.formas_de_pago) else 'method_of_payment' %}
      {% set metodo = frappe.get_doc(pago_dt, p.formas_de_pago) %}
      {# El monto está en la FILA hija p: usa p.monto / p.amount / p.valor; si no hay, reparte o usa el total #}
      {% set monto = p.get('monto') or p.get('amount') or p.get('valor') or (ns.subtotal + ns.ivatotal) %}
      <li>{{ metodo.description or metodo.nombre or metodo.name }} — ${{ "{:,.2f}".format((monto or 0)|float) }}</li>
    {% endfor %}
  </ul>
{% endif %}

<div class="firma">Gracias por su compra</div>

<div class="page-break"></div>

{# ------------------ COMANDA ------------------ #}
<h2 class="centrado">COMANDA</h2>
{% if doc.alias %}
  <p><strong>Alias:</strong> {{ doc.alias }}</p>
{% endif %}
<p><strong>Orden:</strong> {{ doc.name }}</p>
<p><strong>Fecha:</strong> {{ doc.creation }}</p>
{% if doc.table %}
  <p><strong>Mesa:</strong> {{ doc.table }}</p>
{% endif %}

<hr>

<table>
  <thead>
    <tr>
      <th>Producto</th>
      <th style="text-align: center;">Cantidad</th>
    </tr>
  </thead>
  <tbody>
    {% for item in doc.items %}
      {% set producto = frappe.get_doc("Producto", item.product) %}
      <tr>
        <td>{{ producto.nombre }}</td>
        <td style="text-align: center;">{{ item.qty }}</td>
      </tr>
    {% endfor %}
  </tbody>
</table>

<br><br>
<p class="firma">PREPARAR Y ENTREGAR</p>
